function getHeapSortAnimations(array) {
    const animations = [];
    buildMaxHeap(array);
    for (let endIdx = array.length - 1; endIdx > 0; endIdx--) {
        swap(0, endIdx, array);
        siftDown(0, endIdx - 1, array);
    }
    return array;
}

export {getHeapSortAnimations};
